```
/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class FindElements {
   TreeNode nroot;
    public FindElements(TreeNode root) {
        root.val=0;
        helper(root);
        nroot=root;
    }
    public void helper(TreeNode node){
      if(node!=null){

        if(node.left!=null){
          node.left.val=2*node.val+1;
        }

         if(node.right!=null){
          node.right.val=2*node.val+2;
        }

        helper(node.left);
        helper(node.right);

      }
    }

 public boolean find(int target) {
       return  find(nroot,target); 
    }
    public boolean find(TreeNode node,int key){
      if(node==null) return false;

      if(node.val==key) return true;
      boolean l=find(node.left,key);
      boolean r=find(node.right,key);

      return l || r;

    }
}

