class FindElements {
    Set<Integer> treeNodeValues = new HashSet<Integer>();

    public FindElements(TreeNode root) {
        Queue<TreeNode> queue = new LinkedList<TreeNode>();
		queue.add(root);
		root.val = 0;
		TreeNode temp = null;

		while(!queue.isEmpty()) {
			temp = queue.poll();
			treeNodeValues.add(temp.val);

			if(temp.left != null) {
