class FindElements {
public:
    vector <int> num;

    // stor level-order traversal of recover BT in vector....

    FindElements(TreeNode* root) {
        queue<TreeNode*> q;
        if(root != NULL) root->val = 0;
        q.push(root);
        while(!q.empty()){
            auto n = q.front();
            q.pop();
            num.push_back(n->val);

            if(n->left){
                n->left->val = 2*n->val + 1;
                q.push(n->left);
            }
            if(n->right){
                n->right->val = 2*n->val + 2;
                q.push(n->right);
            }
        }
    }

    bool find(int tar) {
        // level-order traversal of recover BT is sorted
        // so vector-num is sorted array
        // now we can apply Binary Search in num...
        int s = 0, e = num.size()-1;
        while(s <= e){
            int mid = s + (e-s)/2;
            if(num[mid] == tar) return true;
            else if(num[mid] > tar) e = mid-1;
            else s = mid+1;
        }
        return false;
    }
};
```

