/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class FindElements {
public:
vector<int> vec;
int s;
    FindElements(TreeNode* root) {
        queue<TreeNode*> q;
        root->val=0;
        q.push(root);
        while(q.size()){
            TreeNode* temp=q.front();
            int x=temp->val;
            q.pop();
            vec.push_back(x);
            if(temp->left){
                temp->left->val=2*x+1;
                q.push(temp->left);
            }
            if(temp->right){
                temp->right->val=2*x+2;
                q.push(temp->right);
            }
        }
        s=vec.size();
    }

    bool find(int target) {
        int ind=lower_bound(vec.begin(),vec.end(),target)-vec.begin();
        if(ind<s && vec[ind]==target) return true;
        return false;
    }
};

