```Python3 []
class FindElements:
    node_list = None
    def __init__(self, root: Optional[TreeNode]):
        self.node_list = set()
        root.val = 0
        self.node_list.add(root.val)
        myq = collections.deque([root])
        l_val = 0
        r_val = 0

        while myq:
            lvl_size = len(myq)

            for _ in range(0, lvl_size):
                node = myq.popleft()
                x = node.val

                if node.left:
                    l_val = 2 * x + 1
                    node.left.val = l_val
                    myq.append(node.left)
                    self.node_list.add(node.left.val)

                if node.right:
                    r_val = 2 * x + 2
                    node.right.val = r_val
                    myq.append(node.right)
                    self.node_list.add(node.right.val)

    def f(self, target: int) -> bool:
        if target in self.node_list:
            return True

    def find(self, target: int) -> bool:
        return self.f(target)
