    public boolean find(int target) {
        target++;
        int high=high(target);
        TreeNode cur=root;
        high=high>>1;
        while(high>0){
            int bit=target&high;
            high=high>>1;
            if(bit!=0&&cur.right!=null){
                cur=cur.right;
            }else if(bit==0&&cur.left!=null){
                cur=cur.left;
            }else{
                return false;
            }
        }
        return true;
    }
    public int high(long target){
        target=target|target>>1;
        target=target|target>>2;
        target=target|target>>4;
        target=target|target>>8;
        target=target|target>>16;
        return (int)((target+1)>>1);
    }
}
