class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {

        // If we want to find greatest sum divisible by 3 then
        // total_sum can be of 3 type total_sum mod 3 = 0 or 1 or 2
        // If total_sum mod 3 is 0 then it is the result
        // If total_sum mod 3 is 1 then we need to remove the least_sum which has least_sum mod 3 = 1 
        // If total_sum mod 3 is 2 then we need to remove the least_sum which has least_sum mod 3 = 2

        // least_sum mod 3 = 1 value 
        int rem1 = 10001;

        // least_sum mod 3 = 2 value
        int rem2 = 10001;

        // total_sum
        int sum = 0;

        for(int i=0;i<nums.size();i++)
        {
            // cumulative sum
            sum+=nums[i];

            // if current number nums[i] is 3 mod 1 then update the reminder1 value with min between num[i] and reminder1
            // Also we update the reminder2 value cause 2 reminder1 value can make 1 reminder2 value EX => 1 + 1 = 2
            if(nums[i]%3==1)rem2 = min(rem1+nums[i],rem2),rem1= min(rem1,nums[i]);

            // if current number nums[i] is 3 mod 2 then update the reminder2 value with min between num[i] and reminder2 
            // Also we update the reminder1 value cause 2 reminder2 value can make 1 reminder1 value EX => 2 + 2 = 4
            if(nums[i]%3==2)rem1 = min(rem2+nums[i],rem1),rem2= min(rem2,nums[i]);
        }

        // If total_sum %3 == 0 return total_sum
        // Otherwise return total_sum - reminder[total_sum%3]

        return (sum%3==0)?sum:((sum%3==1)?(sum-rem1):(sum-rem2));

    }
};
```

