    int pick =nums[i]+ solve(i+1,(curr_sum_rem+nums[i])%3,nums,dp);    
    int notpick =0+ solve(i+1,curr_sum_rem,nums,dp);  
    return dp[i][curr_sum_rem]=max(pick,notpick);

}

