class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {

        int num11 = INT_MAX;
        int num12 = INT_MAX;
        int num21 = INT_MAX;
        int num22 = INT_MAX;

        int sum = 0;
        for(int i = 0; i < nums.size(); i++)
        {
            if(nums[i] % 3 == 1)
            {
                if(nums[i] < num11)
                {
                    num12 = num11;
                    num11 = nums[i];
                }
                else if(nums[i] < num12)
                {
                    num12 = nums[i];
                }
            }
            else if(nums[i] % 3 == 2)
            {
                if(nums[i] < num21)
                {
                    num22 = num21;
                    num21 = nums[i];
                }
                else if(nums[i] < num22)
                {
                    num22 = nums[i];
                }

            }
            sum += nums[i];
        }

        if(sum % 3 == 0)
        {
            return sum;
        }
        else if(sum % 3 == 1)
        {   
            if(num11 == INT_MAX) // If num11 is not set
            {
                return sum - num21 - num22;
            }
            else if(num21 == INT_MAX || num22 == INT_MAX)
            {
                return sum - num11;
            }

            return max(sum - num11, sum - num21 - num22); 
        }

        if(num21 == INT_MAX) // If num21 is not set
        {
            return sum - num11 - num12;
        }
        else if(num11 == INT_MAX || num12 == INT_MAX)
        {
            return sum - num21;
        }
        return max(sum - num21, sum - num11 - num12);

    }
};
```

