public class GreatestSumDivisibleByThreeBottomUp2DApproach {
    public int maxSumDivThree(int[] nums) {
        int[][] maxSum = new int[nums.length + 1][3];

        for (int i = nums.length; i >= 0; i--) {
            for (int target = 2; target >= 0; target--) {
                if (i >= nums.length) {
                    maxSum[i][target] = (target == 0) ? 0 : Integer.MIN_VALUE;
                    continue;
                }

                int difference = target - (nums[i] % 3);

                maxSum[i][target] = Math.max(
                        maxSum[i + 1][(difference >= 0) ? difference : difference + 3] + nums[i],
                        maxSum[i + 1][target]
                );
            }
        }

        return maxSum[0][0];
    }
}
```
```
public class GreatestSumDivisibleByThreeBottomUpConstantSpaceApproach {
    public int maxSumDivThree(int[] nums) {
        int[] maxSum = new int[3];

        for (int i = nums.length; i >= 0; i--) {
            int[] current = new int[3];

            for (int target = 2; target >= 0; target--) {
                if (i >= nums.length) {
                    current[target] = (target == 0) ? 0 : Integer.MIN_VALUE;
                    continue;
                }

                int difference = target - (nums[i] % 3);

                current[target] = Math.max(
                        maxSum[(difference >= 0) ? difference : difference + 3] + nums[i],
                        maxSum[target]
                );
            }

            maxSum = current;
        }

        return maxSum[0];
    }
}
