        return maxSumDivThree(0, 0, nums, new int[nums.length][3]);
    }

    private int maxSumDivThree(int i, int target, int[] nums, int[][] memo) {
        if (i >= nums.length) return target == 0 ? 0 : Integer.MIN_VALUE;
        if (memo[i][target] != 0) return memo[i][target];

        int difference = target - (nums[i] % 3);

        return memo[i][target] = Math.max(
                maxSumDivThree(i + 1, (difference >= 0) ? difference : difference + 3, nums, memo) + nums[i],
                maxSumDivThree(i + 1, target, nums, memo)
        );
    }
}
