class Solution:
    def maxSumDivThree(self, nums: List[int]) -> int:
        # strategy:
        # we sum everything, and then remove the smallest
        # number(s) possible to make the number divisble by 3

		# if sum % 3 == 2, we just remove a number that 
		# is also % 3 == 2, call it mod2, same for mod1.
		# key: removing 2 mod2's is the same as removing 1 mod1 number
		# and vice versa

        # if sum % 3 == 0, then we are good
        # if sum % 3 == 2, we either remove 2 mod1's or 1 mod2
		# if sum % 3 == 1, we remove 2 mod2's or 1 mod1
        # which ever smaller

		# you notice we only need to keep track of the 2 smallest
		# nums for mod1 and mod2, so only O(1) space is needed

        total = 0
        mod1 = [math.inf, math.inf]
        mod2 = [math.inf, math.inf]

        for num in nums:
            if num % 3 == 1:
                if num < mod1[0]:
                    mod1[0], mod1[1] = num, mod1[0]
                elif num < mod1[1]:
                    mod1[1] = num
            elif num % 3 == 2:
                if num < mod2[0]:
                    mod2[0], mod2[1] = num, mod2[0]
                elif num < mod2[1]:
                    mod2[1] = num
            total += num

        if total % 3 == 1:
			# if less than 2 nums in mod2, then we can only take from mod1
            if math.inf in mod2:
                return total - mod1[0]
            total -= min(sum(mod2), mod1[0])
        elif total % 3 == 2:
            if math.inf in mod1:
                return total - mod2[0]
            total -= min(sum(mod1), mod2[0])

        return total
