class Solution:
    def maxSumDivThree(self, nums: List[int]) -> int:

        rem1 = [float('inf')]
        rem2 = [float('inf')]


        ans = sum(nums)

        for item in nums:
            if item%3 == 1:
                rem1.append(item)

            elif item%3 == 2:
                rem2.append(item)

        rem1.sort()
