     	int n = nums.size() ; 

        //store the numbers which give remainder 1 , 2 when div by 3 : 
     	priority_queue<int, vector<int> , greater<int> > one ; 
     	priority_queue<int, vector<int> , greater<int> > two ; 



     	int  tsum = 0 ; 

/*
    what i am doing in the below loop : 
    basically tsum will store the total sum of the nums 

    3 cases arrive : either the tsum % 3 == 0 or 1 or 2 ! 
    so the pq -> one and two will have the nums with remainder 1 and 2 respectively 

*/

     	for(int i =0 ; i < n ; i++){
     		tsum += nums[i] ; 
     		if(nums[i] % 3 == 1){
     			one.push(nums[i]) ; 
     		}
     		if(nums[i] % 3 == 2){
     			two.push(nums[i]) ; 
     		}
     	}

        // best answer 
     	if(tsum % 3 == 0){
     		return tsum ; 
     	}


        // here i have 2 choices : 
        // either subtract 1 num with remainder 1 or 2 numbers with remainder two : 
     	if(tsum % 3 == 1 ){
     		int first = tsum - one.top() , second = 0  ; 

     		if(two.size() >= 2){
     			second += two.top() ;
     			int temp = two.top() ; 
     			two.pop() ; 
     			second += two.top() ; 
     			two.push(temp) ; 
     		}

     		if(second == 0){
     			return first ; 
     		}

     		return max(first , tsum - second ) ; 
     	}


        // here again i have 2 choices : 
        // subtract 1 num with remainder 2 or subtract 2 num with remainder 2 : 
     	if(tsum % 3 == 2 ){
     		int first = tsum - two.top() , second = 0  ; 

