     			second += one.top() ;
     			int temp = one.top() ; 
     			one.pop() ; 
     			second += one.top() ; 
     			one.push(temp) ; 
     		}

     		if(second == 0){
     			return first ; 
     		}

     		return max(first , tsum - second ) ; 
     	}
     	return 0 ; 
    }

};

