class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        int n=nums.size();
        vector<int> v[4];
        int sum=0;
        for(auto it:nums)
        {
            int k=it%3;
            v[k].push_back(it);
            sum+=it;
        }
        if(sum%3==0)return sum;
        for(int i=0;i<4;i++)
            sort(v[i].begin(),v[i].end());
        if(sum%3==1)
        {
            int t1=INT_MIN;
            int t2=INT_MIN;
            if(v[1].size()>0)
                t1=sum-v[1][0];
            if(v[2].size()>1)
