            int c = r2 + nums[i];
            if (a % 3 == 0) {
                nr0 = Math.max(nr0, a);
            } else if (a % 3 == 1) {
                nr1 = Math.max(nr1, a);
            } else if (a % 3 == 2) {
                nr2 = Math.max(nr2, a);
            }

            if (b % 3 == 0) {
                nr0 = Math.max(nr0, b);
            } else if (b % 3 == 1) {
                nr1 = Math.max(nr1, b);
            } else if (b % 3 == 2) {
                nr2 = Math.max(nr2, b);
            }

            if (c % 3 == 0) {
                nr0 = Math.max(nr0, c);
            } else if (c % 3 == 1) {
                nr1 = Math.max(nr1, c);
            } else if (c % 3 == 2) {
                nr2 = Math.max(nr2, c);
            }
            r0=nr0;
            r1=nr1;
            r2=nr2;
        }

        return r0;
    }
}
