class Solution {
public:
    int maxi = 99999999;
    int maxSumDivThree(vector<int>& nums) {
        int one1=maxi, one2=maxi, two1=maxi, two2=maxi; //Variables to track two smallest 3k+1, 3k+2 numbers; 
        int ans = 0;
        if(nums.size()==1 && nums[0]%3) return 0;
        for(int i=0;i<nums.size(); i++){
            ans+= nums[i];
            switch(nums[i]%3){   
                case 1:
                one2 = min(one2, nums[i]);
                if(one2 < one1) swap(one1, one2);
                break;

                case 2:
                two2 = min(two2, nums[i]);
                if(two2 < two1) swap(two1, two2);
                break;  
            }
        }

        if(ans%3==0) return ans;

        if(ans%3 == 1) ans-=min(one1, two1+two2);

        else ans-=min(two1, one1+one2);


        return ans;
    }
};
