    public int maxSumDivThree(int[] nums) {
        PriorityQueue<Integer> minHeap1 = new PriorityQueue<>();
        PriorityQueue<Integer> minHeap2 = new PriorityQueue<>();

        Arrays.stream(nums).boxed().filter(t -> t % 3 == 1).forEach(minHeap1::add);
        Arrays.stream(nums).boxed().filter(t -> t % 3 == 2).forEach(minHeap2::add);

        int sum = Arrays.stream(nums).boxed().reduce(Integer::sum).get();
        int sum0 = Arrays.stream(nums).boxed().filter(t -> t % 3 == 0).reduce(Integer::sum).orElse(0);

        while (sum % 3 != 0) {
            if (sum % 3 == 1) {
                if (!minHeap1.isEmpty()) {
                    if (minHeap2.size() > 1) {
                        int t1 = minHeap2.poll();
                        int t2 = minHeap2.poll();
                        if (t1 + t2 < minHeap1.peek()) {
                            sum = sum - t1 - t2;
                        } else {
                            sum = sum - minHeap1.poll();
                            minHeap2.add(t1);
                            minHeap2.add(t2);
                        }
                    } else {
                        sum = sum - minHeap1.poll();
                    }
                } else if (minHeap1.isEmpty() && minHeap2.size() < 2)
                    return sum0;
                else {
                    sum = sum - minHeap2.poll() - minHeap2.poll();
                }
            } else if (sum % 3 == 2) {
                if (!minHeap2.isEmpty()) {
                    if (minHeap1.size() > 1) {
                        int t1 = minHeap1.poll();
                        int t2 = minHeap1.poll();
                        if (t1 + t2 < minHeap2.peek()) {
                            sum = sum - t1 - t2;
                        } else {
                            sum = sum - minHeap2.poll();
                            minHeap1.add(t1);
                            minHeap1.add(t2);
                        }
                    } else {
                        sum = sum - minHeap2.poll();
                    }
                } else if (minHeap2.isEmpty() && minHeap1.size() < 2)
                    return sum0;
                else {
                    sum = sum - minHeap1.poll() - minHeap1.poll();
                }
            }
        }
        return sum;

    }


