class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        // DP: define size == nums.size() and array dp[3][size + 1] where dp[i][j + 1]
        // denotes the max sum modulo 3 equals i in subarray nums[0:j]. We calculate dp[i][j] accordingly
        // and return the final result of dp[0][size].
        int size = nums.size();
        vector<vector<int>> dp(3, vector<int>(size + 1));

        for (int j = 1; j <= size; ++j)
        {
            int mod = nums[j - 1] % 3;
            if (mod == 0) // nums[j - 1] modulo 3 == 0
            {
                dp[0][j] = nums[j - 1] + dp[0][j - 1];
                dp[1][j] = dp[1][j - 1] == 0 ? 0 : dp[1][j - 1] + nums[j - 1];
                dp[2][j] = dp[2][j - 1] == 0 ? 0 : dp[2][j - 1] + nums[j - 1];
            }
            else if (mod == 1) // nums[j - 1] modulo 3 == 1
            {
                dp[0][j] = max(dp[0][j - 1], dp[2][j - 1] == 0 ? 0 : dp[2][j - 1] + nums[j - 1]);
                dp[1][j] = max(dp[1][j - 1], dp[0][j - 1] + nums[j - 1]);
                dp[2][j] = max(dp[2][j - 1], dp[1][j - 1] == 0 ? 0 : dp[1][j - 1] + nums[j - 1]);
            }
            else // nums[j - 1] modulo 3 == 2
            {
                dp[0][j] = max(dp[0][j - 1], dp[1][j - 1] == 0 ? 0 : dp[1][j - 1] + nums[j - 1]);
                dp[1][j] = max(dp[1][j - 1], dp[2][j - 1] == 0 ? 0 : dp[2][j - 1] + nums[j - 1]);
                dp[2][j] = max(dp[2][j - 1], dp[0][j - 1] + nums[j - 1]);
            }
        }

        return dp[0][size];
    }
};
