            for (int j = 0; j < 3; j++) {
                if (!i) {
                    if (!j) {
                        dp[i][j] = (nums[i]%3 == 0 ? max(nums[i],0) : 0);
                    }
                    else {
                        dp[i][j] = (nums[i]%3 == j ? nums[i] : oo);
