class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n + 1, vector<int>(3, INT_MIN));

        // Initialize the base case
        dp[0][0] = 0;

        for (int i = 1; i <= n; ++i) {
            int remainder = nums[i - 1] % 3;

            for (int j = 0; j < 3; ++j) {
                dp[i][j] = dp[i - 1][j];  // Initialize with the previous state

                // Update dp[i][j] using the current element
                int prev_remainder = (j - remainder + 3) % 3;
                dp[i][j] = max(dp[i][j], dp[i - 1][prev_remainder] + nums[i - 1]);
            }
        }

        return dp[n][0];
}

};
