class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        vector<int> next(3, 0), curr(3, 0);
        int res{};

        next[nums.back() % 3] = nums.back();

        for(int i = nums.size() - 2; i >= 0; i--) {
            int mod = nums[i] % 3;

            for(int j = 0; j < 3; j++) {
                if(next[j] or (mod+j)%3 == mod)
                    curr[(mod+j)%3] = std::max(curr[(mod+j)%3], next[j] + nums[i]);
                curr[j] = std::max(curr[j], next[j]);
            }
            std::swap(curr, next);
        } return next[0];
    }
};
