class Solution:
    def maxSumDivThree(self, nums: List[int]) -> int:


        total=sum(nums)
        q,r=divmod(total,3)

        if r==0:
            return total

        r1=[]
        r2=[]

        for val in nums:
            if val%3==1:
                r1.append(val)
            elif val%3==2:
                r2.append(val)

        r1.sort()
        r2.sort()

        if r==1:
            if not r1:
                return total-(r2[0]+r2[1])
            elif len(r2)<=1:
                return total-(r1[0])
            else:
                pos1=r1[0]
                pos2=r2[0]+r2[1]
