class Solution
{
public:
    int n;
    vector<int> b;
    // pair<int, int> dp[100001][5];
    // t/f, sum
    pair<int, int> f(int i, int s, vector<int> &a, vector<vector<pair<int, int>>> &dp)
    {
        if (i == n)
            return {s % 3 == 0, 0};

        if (dp[i][s] != make_pair(-1, -1))
            return dp[i][s];

        bool take = f(i + 1, (s + b[i]) % 3, a, dp).first;
        int nottake = f(i + 1, s, a, dp).first;
        if (take)
            return dp[i][s] = {take, max(a[i] + f(i + 1, (s + b[i]) % 3, a, dp).second, f(i + 1, s, a, dp).second)};
        else
            return dp[i][s] = {nottake, f(i + 1, s, a, dp).second};
    }
    int maxSumDivThree(vector<int> &a)
    {
        n = a.size();
        b.resize(n);
        // pair<int, int> p;
        // memset(dp, make_pair(-1, -1), sizeof(dp));
        vector<vector<pair<int, int>>> dp(n + 1, vector<pair<int, int>>(5, {-1, -1}));

        for (int i = 0; i < n; i++)
            b[i] = a[i] % 3;

        return f(0, 0, a, dp).second;
    }
};
