                int x0=p/n,y0=p%n;
                for(auto d: dir){
                    int x=x0+d[0],y=y0+d[1];
                    if(x<0||y<0||x>=m||y>=n||g[x][y]!='.'||v[x*n+y]) continue;
                    v[x*n+y]=1;
                    q.push(x*n+y);
                }
            }
        }
        g[box/n][box%n]='.';
        return 0;
    }
