```
public class Solution 
{
    private int[] x = new int[4] { 0, 0, 1, -1 };
    private int[] y = new int[4] { 1, -1, 0, 0 };

    public int MinPushBox(char[][] grid) 
    {
        int bx = 0, by = 0, px = 0, py = 0, tx = 0, ty = 0;

        int m = grid.Length;
        int n = grid[0].Length;

        // Set to maintain the visited positions of the player and box
        HashSet<int> boxSet = new HashSet<int>();
        HashSet<int> playerSet = new HashSet<int>();

        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                if(grid[i][j] == 'B')
                {
                    bx = i;
                    by = j;
                }
                else if(grid[i][j] == 'S')
                {
                    px = i;
                    py = j;
                }
                else if(grid[i][j] == 'T')
                {
                    tx = i;
                    ty = j;
                }
            }
        }

        Queue<Position> queue = new Queue<Position>();

        Position pos = new Position(px, py, bx, by);

        queue.Enqueue(pos);

        boxSet.Add(bx * n + by);

        for(int step = 0; queue.Count > 0; step++)
        {
            for(int size = queue.Count; size > 0; size--)
            {
                pos = queue.Dequeue();

                // Box has reached the target location
