class Solution {
                                    // Up,    Right,   Bottom  & Left
    private int[][] dir = new int[][]{{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

    public int minPushBox(char[][] grid) {
        int step = 0;
        int m = grid.length;
        int n = grid[0].length;
        Queue<int[]> q = new LinkedList<>();
        int[] box,target,player;
        box = target = player = null;

        // TO Store Already Processed Node.
        // Box coordinates with respect to Player's Moves
        boolean[][][] visited = new boolean[m][n][4];

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 'B') box = new int[]{i, j};
                else if (grid[i][j] == 'T') target = new int[]{i, j};
                else if (grid[i][j] == 'S') player = new int[]{i, j};
            }
        }

        q.offer(new int[]{box[0], box[1], player[0], player[1]});
        while (!q.isEmpty()) {
            int size = q.size();
            // Process Each Level Nodes of Queue
            while(size!=0) {
                int[] curr = q.poll();
                // Box reached Target Location Return steps...
                if (curr[0] == target[0] && curr[1] == target[1]) 
                    return step;
                // Explore 4 directions at this point ...
                for (int j = 0; j < dir.length; j++) {

                    if (visited[curr[0]][curr[1]][j]) 
                        continue;

                    int[] d = dir[j];

                    int r0 = curr[0] + d[0];
                    int c0 = curr[1] + d[1];  
                    int r = curr[0] - d[0];
                    int c = curr[1] - d[1];
                    if (!isValid(r0,c0,grid) || !isValid(r,c,grid)) 
                        continue;
                    if (!isReachable(r0, c0, curr, grid)) 
                        continue;

                    visited[curr[0]][curr[1]][j] = true;
                    q.offer(new int[]{r, c, curr[0], curr[1]});
                }
                size--;
            }
            step++;
        }
        return -1;

    }

    // can player reach a position to push ?
    private boolean isReachable(int x, int y, int[] curr, char[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        Queue<int[]> q = new LinkedList<>();
        int playerStartR = curr[2];
        int playerStartC = curr[3];
        q.offer(new int[]{playerStartR, playerStartC});
        boolean[][] visited = new boolean[m][n];
        // mark the box current position as visited // [0:1] ==> Box Pos
        visited[curr[0]][curr[1]] = true;
        while (!q.isEmpty()) {
            int[] temp = q.poll();
            if (temp[0] == x && temp[1] == y) 
                return true;
            for (int[] d : dir) {
                int r = temp[0] + d[0];
                int c = temp[1] + d[1];  
                if (!isValid(r,c,grid) || visited[r][c]) 
                    continue;
                visited[r][c] = true;
                q.offer(new int[]{r, c});
            }
        }
        return false;
    }

    private boolean isValid(int x, int y,char[][] grid){
        int r = grid.length;
        int c = grid[0].length;
        return x>=0 && x<r && y>=0 && y<c && grid[x][y] != '#';
    }

}
