```
class Solution {
    public int minPushBox(char[][] grid) {
        if(grid == null)
            return 0;

        int row = grid.length;
        int col = grid[0].length;
        int[] target = null;
        int[] box = null;
        int[] player = null;

        for(int i=0; i < row; i++) {
            for(int j=0; j < col; j++) {
                if(grid[i][j] == 'T')
                    target = new int[] {i, j};
                if(grid[i][j] == 'S')
                    player = new int[] {i, j};
                if(grid[i][j] == 'B')
                    box = new int[] {i, j};                
            }
        }

        Queue<int[]> q = new ArrayDeque<>();
        Set<List<Integer>> visited = new HashSet<>();

        //Add player and box current location to queue as state
        q.offer(new int[] {player[0], player[1], box[0], box[1]});
        visited.add(new ArrayList<>(Arrays.asList(player[0], player[1], box[0], box[1])));        

        int[][] dirs = new int[][] {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
        int push = 0;

        while(!q.isEmpty()) {
            int size = q.size();

            for(int i=0; i < size; i++) {
                int[] state = q.poll();
                if(state[2] == target[0] && state[3] == target[1])
                    return push;

                for(int[] dir: dirs) {
                    //player need to be here
                    int pX = state[2] - dir[0];
                    int pY = state[3] - dir[1];

                    //To push box in this direction
                    int bX = state[2] + dir[0];
                    int bY = state[3] + dir[1];
                    if(pX < 0 || pX >= row || pY < 0 || pY >= col || 
                       bX < 0 || bX >= row || bY < 0 || bY >= col ||
                       grid[bX][bY] == '#' || grid[pX][pY] == '#' ||
                       visited.contains(new ArrayList<>(Arrays.asList(state[2], state[3], bX, bY))) || 
                       //player can reach the push location given current box location
                       !canReach(state[0], state[1], grid, pX, pY, state[2], state[3])) 
                        continue;

                    //box location updated and player location updated to last box location                    
                    q.offer(new int[] {state[2], state[3], bX, bY}); 
                    visited.add(new ArrayList<>(Arrays.asList(state[2], state[3], bX, bY)));                    
                }
            }
            push++;

        }

        return -1;

    }


    public boolean canReach(int x, int y, char[][] grid, int tX, int tY, int bX, int bY) {
        Queue<int[]> q = new ArrayDeque<>();

        q.offer(new int [] {x, y});
        int[][] dirs = new int[][] {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
        boolean visited[][] = new boolean[grid.length][grid[0].length];
        while(!q.isEmpty()) {
            int size = q.size();

            for(int i=0; i < size; i++){
                int[] state = q.poll();

                if(state[0] == tX && state[1] == tY)
                    return true;

                for(int[] dir: dirs) {
                    int newX = state[0] + dir[0];
                    int newY = state[1] + dir[1];

                    if(newX < 0 || newY < 0 || 
