class Solution {
    int n, m;
    char[][] grid;
    int[][] dirs = new int[][] {
        {1, 0}, {0, 1}, {-1, 0}, {0, -1}
    };
    public int minPushBox(char[][] grid) {
        n = grid.length;
        m = grid[0].length;
        this.grid = grid;
        int[] box = new int[2], target = new int[2], player = new int[2];
        findLocations(box, target, player);

        Queue<int[]> q = new LinkedList();
        q.offer(new int[]{box[0], box[1], player[0], player[1]});
        boolean[][][] visited = new boolean[n][m][4]; // for 4 directions
        int steps = 0;
        while(!q.isEmpty()) {
            int size = q.size();
            while(size-- > 0) {
                int[] cur = q.poll();
                if(cur[0] == target[0] && cur[1] == target[1]) return steps;
                for(int i = 0; i < 4; i++) {
                    int[] newPlayerLoc = new int[]{cur[0] + dirs[i][0], cur[1] + dirs[i][1]};
                    int[] newBoxLoc = new int[]{cur[0] - dirs[i][0], cur[1] - dirs[i][1]};
                    if(visited[cur[0]][cur[1]][i] || isOutOfBounds(newPlayerLoc, newBoxLoc) || !isReachable(newPlayerLoc, cur)) continue;
                    visited[cur[0]][cur[1]][i] = true;
                    q.offer(new int[]{newBoxLoc[0], newBoxLoc[1], cur[0], cur[1]});
                }
            }
            steps++;
        }

        return -1;

    }

    public boolean isReachable(int[] targetPlayerLoc, int[] cur) {
        boolean[][] visited = new boolean[n][m];
        visited[cur[0]][cur[1]] = true;
        visited[cur[2]][cur[3]] = true;

        Queue<int[]> q = new LinkedList();
        q.offer(new int[]{cur[2], cur[3]});
        while(!q.isEmpty()) {
            int[] playerLoc = q.poll();
            if(playerLoc[0] == targetPlayerLoc[0] && playerLoc[1] == targetPlayerLoc[1]) return true;
            for(int[] d: dirs) {
                int x = playerLoc[0] + d[0];
                int y = playerLoc[1] + d[1];
                if(isOutOfBounds(x, y) || visited[x][y]) continue;
                visited[x][y] = true;
                q.offer(new int[]{x, y});
            } 
        }
        return false;
    }

    public boolean isOutOfBounds(int[] player, int[] box) {
        return isOutOfBounds(player[0], player[1]) || isOutOfBounds(box[0], box[1]);
    }

    public boolean isOutOfBounds(int x, int y) {
        return (x < 0 || y < 0 || x == n || y == m || grid[x][y] == '#');
    }

    public void findLocations(int[] box, int[] target, int[] player) {
        boolean p = false, t = false, b = false;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(grid[i][j] == 'S') {
                    player[0] = i;
                    player[1] = j;
                    p = true;
                } else if(grid[i][j] == 'T') {
                    target[0] = i;
                    target[1] = j;
                    t = true;
                } else if(grid[i][j] == 'B') {
                    box[0] = i;
                    box[1] = j;
                    b = true;
                }

                if(p && b && t) return; // found all
            }
        }
    }
}
