//{'#','.','.','.'}
//{'.','.','S','.'}
//{'.','B','.','T'}
//{'.','.','.','#'}
//We can start from the current Box location & do a BFS.
//We don't really need to move player, we can just simulate it.
//In each level, we can try to find valid positions for box in all 4 directions around it
//& if those positions have a valid position around it for player then,
//we can push those positions to Q.
//Box & player new positions valid if: valid boundary/no wall/player can move to the pos.
//In each step, we can try finding all valid player positions in grid using DFS before hand.
//The level where we reach the target will be our minimum moves.
//*********Key is
//if we want to push box to left, player must stand at right, to simulate this 
//we can just use box position & current direction & add current direction to box position
//& subtract current direction from box position to get new box & player positions. 
//we can ignore the player position to simulate the player can move to any position
//int bxNew = boxPos.X + d[0], byNew = boxPos.Y + d[1];
//int pxNew = boxPos.X - d[0], pyNew = boxPos.Y - d[1];
//D[1,0] B[1,1] P[0,1] =>move box left[0,-1]
//=> D[1,0] B[1,0(1-1)] P[1(1+0),2(1-(-1)]
public class MBM2
{
    static readonly List<int[]> Dirs = new List<int[]>
    {new[] { -1, 0 }, new[] { 1, 0 }, new[] { 0, 1 }, new[] { 0, -1 }};
    public static int MinPushBox(char[][] grid)
    {
        Position box = null, target = null, player = null;
        #region Get the box, player & target positions
        int rows = grid.Length, cols = grid[0].Length;
        for (var row = 0; row < rows; row++)
        {
            for (var col = 0; col < cols; col++)
            {
                if (grid[row][col] == 'B')
                    box = new Position(row, col);
                else if (grid[row][col] == 'T')
                    target = new Position(row, col);
                else if (grid[row][col] == 'S')
                    player = new Position(row, col);
            }
        }
        #endregion

        //Do BFS to find the min moves
        //we can save block & player encoded positions int the Q
        var startPos = Position.GetPositionsAsString(box, player);
        var q = new Queue<string>();
        q.Enqueue(startPos);
        //We use a set to record all the positions we have visited
        //(including the position of the player)
        var visitedSet = new HashSet<string>();
        visitedSet.Add(startPos);

        var res = 0;
        while (q.Any())
        {
            res++;
            var size = q.Count();
            while (size > 0)
            {
                size--;
                var decodedPositions = Position.GetPositions(q.Dequeue());
                var boxPos = decodedPositions.Key;
                var playerPos = decodedPositions.Value;
                var canVisit = new bool[rows, cols];

                //Use dfs to find all the positions that the player can reach at this moment. 
                UpdatePlayerReachablePositions(grid, canVisit, playerPos, boxPos);

                //Move the box and update the set
                foreach (var d in Dirs)
                {
                    //if we want to push box to right, storekeeper must stand at left
                    int bxNew = boxPos.X + d[0], byNew = boxPos.Y + d[1];
                    int pxNew = boxPos.X - d[0], pyNew = boxPos.Y - d[1];
                    var newBoxPos = new Position(bxNew, byNew);
                    var newPlayerPos = new Position(pxNew, pyNew);

                    //Box cannot move to this direction in any of the following condition 
                    if (!newPlayerPos.IsValid(grid) || !newBoxPos.IsValid(grid) ||
                        newBoxPos.IsWall(grid) || !canVisit[pxNew, pyNew])
                        continue;

                    //If we have reached destination return
                    if (newBoxPos.Equals(target))
                        return res;

                    //Now we can move the box, also record the new position(including the original position of the box)
                    var newPosition = Position.GetPositionsAsString(newBoxPos, newPlayerPos);
                    //Make sure the newPosition has never been visited before
                    if (visitedSet.Add(newPosition))
                    {
                        q.Enqueue(newPosition);
                    }
                }
            }
        }
        return -1;
    }
    //DFS to find locations the player can reach
    public static void UpdatePlayerReachablePositions(char[][] grid, bool[,] canVisit, Position playerPosition, Position boxPosition)
    {
        if (!playerPosition.IsValid(grid) ||
            playerPosition.IsWall(grid) ||
            playerPosition.Equals(boxPosition) ||
            canVisit[playerPosition.X, playerPosition.Y])
            return;
        canVisit[playerPosition.X, playerPosition.Y] = true;
        foreach (var d in Dirs)
        {
            var newPlayerPos = new Position(playerPosition.X + d[0], playerPosition.Y + d[1]);
            UpdatePlayerReachablePositions(grid, canVisit, newPlayerPos, boxPosition);
        }
    }

    public class Position
    {
        public int X { get; set; }
        public int Y { get; set; }
        public Position(int x, int y)
        {
            X = x;
            Y = y;
        }
        public static string GetPositionsAsString(Position box, Position storeKeeper)
        {
            return box.ToString() + "|" + storeKeeper.ToString();
        }

        public static KeyValuePair<Position, Position> GetPositions(string num)
        {
            var pos = num.Split('|');
            var res = new KeyValuePair<Position, Position>(new Position(pos[0], pos[1]), new Position(pos[2], pos[3]));
            return res;
        }
        public Position(string x, string y)
        {
            int.TryParse(x, out var x1);
            X = x1;
            int.TryParse(y, out var y1);
            Y = y1;
        }
        public override string ToString()
        {
            return X + "|" + Y;
        }

        public bool IsValid(char[][] grid)
        {
            return X >= 0 && Y >= 0 && X < grid.Length && Y < grid[0].Length;
        }

        public bool IsWall(char[][] grid)
        {
            return grid[X][Y] == '#';
        }

        public bool Equals(Position position)
        {
            return X == position.X && Y == position.Y;
        }
    }
}

