from collections import deque

class Solution:
    def minPushBox(self, grid: List[List[str]]) -> int:

        in_bounds = lambda grid, i, j : 0 <= i < len(grid) and 0 <= j < len(grid[0])

        dirs = [
            (1, 0),
            (-1, 0),
            (0, 1),
            (0, -1)
        ]

        def mover_bfs(grid, mover, behind_box, box):

            queue = deque([mover])
            visited = set()

            while queue:
                move_pos = queue.popleft()

                if move_pos in visited:
                    continue

                visited.add(move_pos)

                if move_pos == behind_box:
                    return True
                x, y = move_pos
                for d in dirs:
                    r, c = x + d[0], y + d[1]

                    if not in_bounds(grid, r, c) or (r, c) == box or grid[r][c] == '#':
                        continue

                    queue.append((r, c))
            return False

        def box_bfs(grid, box, start, dest):
            queue = deque([ (box, start, 0) ])
            visited = set()
            while queue:
                box_pos, mover_pos, moves = queue.popleft()
                if box_pos == dest:
                    return moves

                if (box_pos, mover_pos) in visited:
                    continue

                visited.add((box_pos, mover_pos))

                x, y = box_pos
                x1, y1 = mover_pos

                for d in dirs:
                    r, c = x + d[0], y + d[1]

                    if not in_bounds(grid, r, c) or grid[r][c] == '#' or (r, c) in visited:
                        continue

                    next_mover_pos = x + (-1) * d[0], y + (-1) * d[1]
                    if not mover_bfs(grid, mover_pos, next_mover_pos, box_pos):
                        continue
                    queue.append(((r, c), next_mover_pos, moves + 1))
            return -1

        start = None
        box = None
        target = None

        rows = len(grid)
        cols = len(grid[0])

        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 'S':
                    start = (r, c)
                elif grid[r][c] == 'T':
                    target = (r, c)
                elif grid[r][c] == 'B':
                    box = (r, c)

        return box_bfs(grid, box, start, target)
