class Solution {
    public int minPushBox(char[][] grid) {
        if (grid == null || grid.length == 0 || grid[0].length == 0) {
            return -1;
        }
        int[] box = new int[2];
        int[] player = new int[2];
        int[] target = new int[2];
        Map<String, Integer> steps = new HashMap<>();
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                if (grid[i][j] == 'B') {
                    box = new int[] {i, j};
                } else if (grid[i][j] == 'S') {
                    player = new int[] {i, j};
                } else if (grid[i][j] == 'T') {
                    target = new int[] {i, j};
                }
            }
        }
        int[] dx = new int[] {0, 0, 1, -1};
        int[] dy = new int[] {1, -1, 0, 0};
        // so in BFS, instead of save a single point, save both box and player together
        String start = encode(box[0], box[1], player[0], player[1]);
        int res = Integer.MAX_VALUE;
        Queue<String> queue = new LinkedList<>();
        queue.offer(start);
        steps.put(start, 0);
        while (!queue.isEmpty()) {
            String curr = queue.poll();
            if (steps.getOrDefault(curr, 0) > res) {
                continue;
            }
            int[] currBox = new int[] {Integer.parseInt(curr.split(":")[0]), Integer.parseInt(curr.split(":")[1])};
            int[] currPlayer = new int[] {Integer.parseInt(curr.split(":")[2]), Integer.parseInt(curr.split(":")[3])};
            if (currBox[0] == target[0] && currBox[1] == target[1]) {
                res = Math.min(res, steps.get(curr));
                continue;
            }
            for (int i = 0; i < 4; i++) {
                // every step, move player, if player meet the box, push the box.
                int[] newP = new int[] {currPlayer[0] + dx[i], currPlayer[1] + dy[i]};
                if (newP[0] < 0 || newP[1] < 0 || newP[0] >= grid.length 
                    || newP[1] >= grid[0].length || grid[newP[0]][newP[1]] == '#') {
                    continue;
                }
                if (newP[0] == currBox[0] && newP[1] == currBox[1]) {
                    int[] newB = new int[] {currBox[0] + dx[i], currBox[1] + dy[i]};
                    if (newB[0] < 0 || newB[1] < 0 || newB[0] >= grid.length 
                        || newB[1] >= grid[0].length || grid[newB[0]][newB[1]] == '#') {
                        continue;
                    }
                    String temp = encode(newB[0], newB[1], newP[0], newP[1]);
                    if (steps.containsKey(temp) && steps.get(temp) <= steps.get(curr) + 1) {
                        continue;
                    }
                    steps.put(temp, steps.get(curr) + 1);
                    queue.offer(temp);
                } else {
                    // if not meet box, just save it to the queue.
                    String temp = encode(currBox[0], currBox[1], newP[0], newP[1]);
                    if (steps.containsKey(temp) && steps.get(temp) <= steps.get(curr) + 1) {
                        continue;
                    }
                    steps.put(temp, steps.get(curr));
                    queue.offer(temp);
                }
            }   
        }
        return res == Integer.MAX_VALUE ? -1 : res;
    }

    private String encode(int i1, int i2, int i3, int i4) {
        return i1 + ":" + i2 + ":" + i3 + ":" + i4;
    }
}
