    def minPushBox(self, grid: List[List[str]]) -> int:

        # check if player can move from start to end given the ball's position
        def can_move(start, end, ball):
            if not (0 <= end[0] < rows and 0 <= end[1] < cols):
                return False
            q = collections.deque([start])
            seen = {start}
            while q:
                row, col = q.popleft()
                if (row, col) == end:
                    return True
                for r_offset, c_offset in self.offsets:
