        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 'T':
                    target = (r, c)
                elif grid[r][c] == 'S':
                    source = (r, c)
                elif grid[r][c] == 'B':
                    ball = (r, c)

        # dfs starting from source, ball
        seen = {(source, ball)}
        q = collections.deque([(0, source, ball)])

        while q:
            dist, source, ball = q.popleft()

            if ball == target:
                return dist

            for r_offset, c_offset in self.offsets:
                r, c = ball[0] + r_offset, ball[1] + c_offset

