// We need to track the min number of steps that the box needs to be pushed to the target
// We don't care how many steps the player takes
// We will track the state of the box and the player
// For every box position we consider each possible next box position
//    - The usual checks, is the box position in bounds, visited, not a wall, etc. plus...
//    - Can the player be moved into position to move the box to the new position? Use separate BFS
// We track the visited position and direction of the box
// 4 ms, faster than 99.60%
class State {
  int pRow;
  int pCol;
  int bRow;
  int bCol;
  State() {}
  State(int pRow, int pCol, int bRow, int bCol) {
    this.pRow = pRow;
    this.pCol = pCol;
    this.bRow = bRow;
    this.bCol = bCol;
  }
}
class Solution {
  private static final int[][] DIRS = new int[][]{{0,1},{1,0},{0,-1},{-1,0}};
  public int minPushBox(char[][] grid) {
    int rows = grid.length;
    int cols = grid[0].length;
    State initState = new State();
    for (int row = 0; row < rows; row++) {
      for (int col = 0; col < cols; col++) {
        if (grid[row][col] == 'S') {
          initState.pRow = row;
          initState.pCol = col;
        } else if (grid[row][col] == 'B') {
          initState.bRow = row;
          initState.bCol = col;
        }
      }
    }
    boolean[][][] visited = new boolean[rows][cols][4];
    Deque<State> deque = new ArrayDeque<>();
    deque.add(initState);
    int steps = 1;
    while (!deque.isEmpty()) {
      int size = deque.size();
      for (int i = 0; i < size; i++) {
        State curr = deque.remove();
        for (int dir = 0; dir < 4; dir++) {
          int adjBRow = curr.bRow + DIRS[dir][0];
          int adjBCol = curr.bCol + DIRS[dir][1];
          // player must have been in this position to push box to new adj position
          int adjPRow = curr.bRow - DIRS[dir][0]; 
          int adjPCol = curr.bCol - DIRS[dir][1];
          if (adjBRow < 0 || adjBRow > rows - 1 || adjBCol < 0 || adjBCol > cols - 1 || 
             adjPRow < 0 || adjPRow > rows - 1 || adjPCol < 0 || adjPCol > cols - 1 ||
              visited[adjBRow][adjBCol][dir] || 
              grid[adjBRow][adjBCol] == '#' || grid[adjPRow][adjPCol] == '#' || 
             !playerCanReachBox(grid, curr.pRow, curr.pCol, adjPRow, adjPCol, curr.bRow, curr.bCol))
            continue;
          if (grid[adjBRow][adjBCol] == 'T')
            return steps;
          visited[adjBRow][adjBCol][dir] = true;
          deque.add(new State(curr.bRow, curr.bCol, adjBRow, adjBCol));
        }
      }
      steps++;
    }
    return -1;
  }
  private boolean playerCanReachBox(char[][] grid, int pRow1, int pCol1, int pRow2, int pCol2, int bRow, int bCol) {
    int rows = grid.length;
    int cols = grid[0].length;
    if (pRow1 == pRow2 && pCol1 == pCol2)
      return true;
    Deque<Integer> deque = new ArrayDeque<>();
    deque.add(pRow1 * cols + pCol1);
    boolean[][] visited = new boolean[rows][cols];
    visited[pRow1][pCol1] = true;
    visited[bRow][bCol] = true;
    while (!deque.isEmpty()) {
      int index = deque.remove();
      int row = index / cols;
      int col = index % cols;
      for (int[] dir : DIRS) {
        int adjRow = row + dir[0];
        int adjCol = col + dir[1];
        if (adjRow < 0 || adjRow > rows - 1 || adjCol < 0 || adjCol > cols - 1 ||
            visited[adjRow][adjCol] || grid[adjRow][adjCol] == '#')
          continue;
        if (adjRow == pRow2 && adjCol == pCol2)
          return true;
        deque.add(adjRow * cols + adjCol);
        visited[adjRow][adjCol] = true;
      }
    }
    return false;
  }
}
