An ugly implementation, but since didn't see a javascript implementation of this yet decided to post mine up to see if it helps someone. The priorityqueue implementation was straight lifted from a stack overflow post. Basically a* search is bfs + heuristics + priorityqueue. Used the same heuristic that many other users are using which is just the manhattan distance + number of moves thus far.

Originally I try to just solve this without using a priorityqueue and just sorting the array at every loop, but the time penalty was too great (I naively thought time complexity will remain the same assuming that nlogn of sort is equivalent to heapifying n moves, but I realized that at each instance you are only really considering 5 new states to push in to the queue so it's not the same). Also, now looking at it using Map for states was probably not necessary, probably would've been better to use a Set and use...
((block[0]*grid[0].length)+block[1])*(grid[0].length*grid.length) +((character[0]*grid[0].length)+character[1])

```
/**
 * @param {character[][]} grid
 * @return {number}
 */
const top = 0;
const parent = i => ((i + 1) >>> 1) - 1;
const left = i => (i << 1) + 1;
const right = i => (i + 1) << 1;

class PriorityQueue {
  constructor(comparator = (a, b) => a > b) {
    this._heap = [];
    this._comparator = comparator;
  }
  size() {
    return this._heap.length;
  }
  isEmpty() {
    return this.size() == 0;
  }
  peek() {
    return this._heap[top];
  }
  push(...values) {
    values.forEach(value => {
      this._heap.push(value);
      this._siftUp();
    });
    return this.size();
  }
  pop() {
    const poppedValue = this.peek();
    const bottom = this.size() - 1;
    if (bottom > top) {
      this._swap(top, bottom);
    }
    this._heap.pop();
    this._siftDown();
    return poppedValue;
  }
  replace(value) {
    const replacedValue = this.peek();
    this._heap[top] = value;
    this._siftDown();
    return replacedValue;
  }
  _greater(i, j) {
    return this._comparator(this._heap[i], this._heap[j]);
  }
  _swap(i, j) {
    [this._heap[i], this._heap[j]] = [this._heap[j], this._heap[i]];
  }
  _siftUp() {
    let node = this.size() - 1;
    while (node > top && this._greater(node, parent(node))) {
      this._swap(node, parent(node));
      node = parent(node);
    }
  }
  _siftDown() {
    let node = top;
    while (
      (left(node) < this.size() && this._greater(left(node), node)) ||
      (right(node) < this.size() && this._greater(right(node), node))
    ) {
      let maxChild = (right(node) < this.size() && this._greater(right(node), left(node))) ? right(node) : left(node);
      this._swap(node, maxChild);
      node = maxChild;
    }
  }
}

var minPushBox = function(grid) {
    if(typeof(grid)==='undefined' || grid===null
       || grid.length===0 || grid[0].length===0){
        return -1;
    }

    let TARGET = null;
    let startBlk = null;
    let startPer = null;
    const DIR = [
        [0,1], [1,0], [0,-1], [-1,0]
    ];

    for(let i = 0; i<grid.length; i++){
        for(let j = 0; j<grid[0].length; j++){
            if(grid[i][j]==='S'){
                startPer = [i,j];
                grid[i][j]='.';
            }
            if(grid[i][j]==='T'){
                TARGET = [i,j];
            }
            if(grid[i][j]==='B'){
                startBlk = [i,j];
                grid[i][j]='.';
            }
        }
    }

    let queue = new PriorityQueue((a,b)=>a.weight<b.weight);
    let states = new Map();
    queue.push({weight: manDist(startBlk), block: startBlk, character: startPer, move: 0});
    while(!queue.isEmpty()){
        let {weight,block,character,move} = queue.pop();
        if(TARGET[0]===block[0] && TARGET[1]===block[1]){
            return move;
        }
        let key = (block[0]*grid[0].length)+block[1];
        let val = (character[0]*grid[0].length)+character[1];
        if(!states.has(key)){
            states.set(key,new Set());
        }
        states.get(key).add(val);
        DIR.forEach(d=>{
            let i = d[0]+character[0];
            let j = d[1]+character[1];
            let curV = (i*grid[0].length)+j;
            if(validMove(i,j,block[0],block[1]) && !states.get(key).has(curV)){
                queue.push({weight: manDist(block)+move, block: block, character: [i,j], move: move});
            }
        });
        let pushDir = tryPush(character,block);
        if(pushDir!==null){
            let newBlk = [block[0]+pushDir[0],block[1]+pushDir[1]];
            let newCha = [character[0]+pushDir[0],character[1]+pushDir[1]];
            let nBK = (newBlk[0]*grid[0].length)+newBlk[1];
            let nVal = (newCha[0]*grid[0].length)+newCha[1];
            if(!states.has(nBK) || !states.get(nBK).has(nVal)){
                queue.push({weight: manDist(newBlk)+(move+1), block: newBlk, character: newCha, move: move+1});  
            }
        }
    }

    return -1;


    function manDist(block){
        let [x,y] = TARGET;
        let [i,j] = block;
        return Math.abs(x-i) + Math.abs(y-j);
    }
    function validMove(i,j,x=null,y=null){
        if(i<0 || j<0 || i>=grid.length || 
           j>=grid[0].length){
            return false;
        }
        if(((x!==null&&(i===x)) && (y!==null&&(j===y))) || grid[i][j]==='#'){
            return false;
        }
        return true;
    }
    function tryPush(c,b){
        let [i,j] = c;
        let [x,y] = b;
        for(let u = 0; u<DIR.length; u++){
            let [v,w] = DIR[u];
            if(((Math.abs(x-i)===1 && y===j) || (Math.abs(y-j)===1 && x===i)) && validMove(i+v,j+w) && validMove(x+v,y+w) && (i+v)===x && (j+w)===y){
                return [v,w];
            }
        }
        return null;
    }
};
