class Solution {
public:

    vector<int> dx = {0, 1, 0, -1};
    vector<int> dy = {1, 0, -1, 0};
    pair<int, int> B;
    pair<int, int> P;
    pair<int, int> T;

    struct node {
        int x;
        int y;
        int px;
        int py;
        node(int x, int y, int px, int py) : x(x), y(y), px(px), py(py) {

        };
        string to_string() {
            return std::to_string(x) + "-" + 
                   std::to_string(y) + "-" + 
                   std::to_string(px) + "-" + 
                   std::to_string(py);

        }
    };

    bool inside(vector<vector<char>>& grid, int x, int y) {
        return x >= 0 && y >= 0 && x < grid.size() && y < grid[0].size();
    }

    bool canReachPosition(vector<vector<char>>& grid, 
                          int px, int py,
                          int tx, int ty, 
                          int bx, int by) {
        if(!inside(grid, tx, ty)) return false;
        if(grid[tx][ty] == '#') return false;

        int m = grid.size(), n = grid[0].size();

        vector<vector<bool>> seen(m, vector<bool>(n, false));
        queue<pair<int, int>> q;
        q.push({px, py}); 
        seen[px][py] = true;
        seen[bx][by] = true;

        while(!q.empty()) {
            int x = q.front().first, y = q.front().second; q.pop();
            if(x == tx && y == ty) return true;

            for(int k = 0; k < 4; k++) {
                int nx = x + dx[k], ny = y + dy[k];
                if(!inside(grid, nx, ny)) continue;
                if(seen[nx][ny] || grid[nx][ny] == '#') continue;
                q.push({nx, ny});
                seen[nx][ny] = true;
            }
        }
        return false;
    }

    vector<node> expand(vector<vector<char>>& grid, node n) {
        vector<node> nodes;
        int bx = n.x,  by = n.y;
        int px = n.px, py = n.py;

        for(int k = 0; k < 4; k++) {
            int nbx = bx + dx[k], nby = by + dy[k];
            int tx  = bx - dx[k], ty  = by - dy[k];

            if(!inside(grid, nbx, nby)) continue;
            if(grid[nbx][nby] == '#') continue;

            if(canReachPosition(grid, px, py, tx, ty, bx, by))
                nodes.push_back(node(nbx, nby, bx, by));
        }
        return nodes;
    }
    bool canReachBox(vector<vector<char>>& grid) {
        int px = P.first, py = P.second;
        int bx = B.first, by = B.second;
        for(int k = 0; k < 4; k++) {
            int tx = bx + dx[k];
            int ty = by + dy[k];
            if(!inside(grid, tx, ty)) continue;
            if(grid[tx][ty] == '#') continue;
            if(canReachPosition(grid, px, py, tx, ty, bx, by)) return true;
        }
        return false;
    }

    int minPushBox(vector<vector<char>>& grid) {
        int m = grid.size(), n = grid[0].size();

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(grid[i][j] == 'B') {
                    B = make_pair(i, j);
                    grid[i][j] = '.';
                } 
                else if(grid[i][j] == 'S') {
                    P = make_pair(i, j);
                    grid[i][j] = '.';
                } else if(grid[i][j] == 'T') {
                    T = make_pair(i, j);
                    grid[i][j] = '.';
                } 
            }
        }
        if(!canReachBox(grid)) return -1;


        queue<node> q;
        unordered_set<string> seen;
        for(int k = 0; k < 4; k++) {
            int tx = B.first + dx[k];
            int ty = B.second + dy[k];
            if(!canReachPosition(grid, P.first, P.second, tx, ty, B.first, B.second)) 
                continue;
            auto nd = node(B.first, B.second, tx, ty);
            q.push(nd);
            seen.insert(nd.to_string());
        }
        int steps = 0;
        while(!q.empty()) {
            for(int t = q.size(); t > 0; t--) {
                auto node = q.front(); q.pop();
                int x = node.x, y = node.y;
                if(x == T.first && y == T.second) return steps;
                auto nodes = expand(grid, node);

                for(auto &node : nodes) {
                    if(seen.count(node.to_string())) continue;
                    q.push(node);
                    seen.insert(node.to_string());
                }   
            }
            steps++;
        }
        return -1;
    }
};

