class Solution:
    def minPushBox(self, grid: List[List[str]]) -> int:
        m, n = len(grid), len(grid[0])
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 'B': box = (i, j)
                if grid[i][j] == 'T': target = (i, j)
                if grid[i][j] == 'S': player = (i, j)

        if target == box: return 0

        def can_get(cur_b, cur_p, pos): # can player get to a certain position pos
            if cur_p == pos: return True
            seen = {cur_p}
            q = [cur_p]
            for x, y in q:
                for X, Y in ([(x-1, y), (x+1, y), (x, y-1), (x, y+1)]):
                    if 0 <= X < m and 0 <= Y < n and (X, Y) not in seen and grid[X][Y] != '#' and (X, Y) != cur_b:
                        if (X, Y) == pos:
                            return True
                        seen.add((X, Y))
                        q.append((X, Y))
            return False

        q, seen, ans = {(box, player)}, {(box, player)}, 0
        dire = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        while q:
            tmp = []
            ans += 1
            for b, p in q:
                for d in dire:
                    nb = (b[0] + d[0], b[1] + d[1])
                    oppo = (b[0] - d[0], b[1] - d[1]) # player is required to be the opposite side of the box to push
                    if 0 <= nb[0] < m and 0 <= nb[1] < n and grid[nb[0]][nb[1]] != '#' and can_get(b, p, oppo) and (nb, b) not in seen:
                        tmp.append((nb, b))
            q = set(tmp)
            seen |= q
            for d in dire:
                if (target, (target[0]+d[0], target[1]+d[1])) in seen:
                    return ans

        return -1
