        m=grid.length;
        n=grid[0].length;
        int sx=0,sy=0,bx=0,by=0,tx=0,ty=0;
        for(int i=0;i<m;i++) for(int j=0;j<n;j++){
            if(grid[i][j]=='S'){sx=i;sy=j;grid[i][j]='.';}
            if(grid[i][j]=='T'){tx=i;ty=j;grid[i][j]='.';}
