                        if(visited[x][y][dd]) continue;
                        visited[x][y][dd]=true;
                        q.offer(new int[]{x,y,dd});
                    }
                }
                //grid[x][y]='.';
                //grid[v[0]][v[1]]='B';
            }
            depth++;
        }
        return -1;
    }

    private boolean valid(char[][] graph,int sx,int sy,int tx,int ty,int bx,int by){
