                    if(visited[x][y]) continue;
                    visited[x][y]=true;
                    q.offer(new int[]{x,y});
                }
            }

        }
        return false;
    }


    private int[][] dirs={{-1,0},{1,0},{0,1},{0,-1}};
    private int[] trans(int dir){
