class Solution {
public:
    int minPushBox(vector<vector<char>>& grid) {
        int nRows = grid.size(), nCols = grid[0].size();
        int px,py, bx,by;
        for(int i=0;i<nRows; i++) {
            for(int j=0;j<nCols;j++) {
                if(grid[i][j] == 'S') {
                    px = i; py = j;
                }
                if(grid[i][j] == 'B') {
                    bx = i; by = j;
                }
            }
        }

        deque<vector<int>> queue;
        queue.push_back({px,py,bx,by});

        int numPush[21][21][21][21];
        memset(numPush, -1, sizeof(numPush));


        numPush[px][py][bx][by] = 0;

        while(!queue.empty()) {
            auto state = queue.front();
            queue.pop_front();

            px = state[0], py = state[1];
            bx = state[2], by = state[3];
            if(grid[bx][by] == 'T') return numPush[px][py][bx][by];

            // cout<<px<<" "<<py<<" "<<bx<<" "<<by<<" "<<numPush[px][py][bx][by]<<endl;

            vector<pair<int,int>> nextPlayerPos = {{px+1, py}, {px-1,py}, {px,py+1}, {px,py-1}};
            for(auto [nextPx, nextPy]: nextPlayerPos) {
                if(nextPx < 0 || nextPy < 0 || nextPx == nRows || nextPy == nCols || grid[nextPx][nextPy] == '#')
                    continue;
                int nextBx = bx, nextBy = by;
                if(nextPx == bx && nextPy == by) { 
                    nextBx+=(nextPx - px); nextBy+=(nextPy-py);
                }

                if(nextBx < 0 || nextBy < 0 || nextBx == nRows || nextBy == nCols || grid[nextBx][nextBy] == '#') continue;

                int& pushCnt =  numPush[nextPx][nextPy][nextBx][nextBy];
                int prevPushCnt = numPush[px][py][bx][by];
                vector<int> nextState = {nextPx, nextPy, nextBx, nextBy};

                if(pushCnt != -1) continue;

                if(bx == nextBx && by == nextBy) {
                    pushCnt = prevPushCnt;
                    queue.push_front(nextState);
                } else {
                    pushCnt = prevPushCnt+1;
                    queue.push_back(nextState);
                }

            }

        }

        return -1;

    }
};
