```C++ []
typedef pair<int, int> PII;

struct Node {
    PII b_pos;
    PII p_pos;
    int steps;
    Node(PII b_pos_, PII p_pos_, int step) : b_pos(b_pos_), p_pos(p_pos_), steps(step) {}
};
class Solution {
public:
    int dx[4] = {0, 1, 0, -1};
    int dy[4] = {1, 0, -1, 0};
    int row = 0, col = 0;
    PII target_pos;

    int minPushBox(vector<vector<char>>& grid) {
        row = grid.size(), col = grid[0].size();
        PII box_pos, player_pos;
        for(int i = 0; i<row; i++) {
            for(int j = 0; j<col; j++) {
                if(grid[i][j] == 'T') {
                    target_pos.first = i, target_pos.second = j;
                } else if(grid[i][j] == 'B') {
                    box_pos.first = i, box_pos.second = j;
                } else if(grid[i][j] == 'S') {
                    player_pos.first = i, player_pos.second = j;
                }
            }
        }
        auto can_get = [&](PII player_pos_func, PII box_pos_func, PII tar_pos) -> bool {
            if(player_pos_func.first == tar_pos.first && player_pos_func.second == tar_pos.second) return true;
            queue<PII> que;
            que.push(player_pos_func);
            bool sta[25][25];
            memset(sta, false, sizeof sta);
            sta[player_pos_func.first][player_pos_func.second] = true;
            while(que.size()) {
                auto cur_player_pos = que.front();
                que.pop();
                int cur_p_x = cur_player_pos.first, cur_p_y = cur_player_pos.second;
                for(int i = 0; i<4; i++) {
                    int x = cur_p_x + dx[i], y = cur_p_y + dy[i];
                    if(x<0 || x>=row || y<0 || y>=col || grid[x][y] == '#' || sta[x][y]) continue;
                    if(x == box_pos_func.first && y == box_pos_func.second) continue;
                    if(x == tar_pos.first && y == tar_pos.second) return true;
                    que.push({x, y});
                    sta[x][y] = true;
                }
            }
            return false;
        };
        queue<Node> q;
        q.push({box_pos, player_pos, 0});
        bool st[25][25][25][25];
        memset(st, false, sizeof st);
        while(q.size()) {
            auto cur = q.front();
            q.pop();
            PII cur_box_pos = cur.b_pos, cur_player_pos = cur.p_pos;
            int cur_step = cur.steps;
            for(int i = 0; i<4; i++) {
                int box_next_x = cur_box_pos.first + dx[i], box_next_y = cur_box_pos.second + dy[i];
                if(box_next_x<0 || box_next_x>=row || box_next_y<0 || box_next_y>=col || grid[box_next_x][box_next_y] == '#') {
                    continue;
                }
                int player_next_x, player_next_y;
                if(dx[i] == 0) {
                    player_next_x = cur_box_pos.first, player_next_y = cur_box_pos.second - dy[i];
                }
                if(dy[i] == 0) {
                    player_next_x = cur_box_pos.first - dx[i], player_next_y = cur_box_pos.second;
                }
                if(player_next_x<0 || player_next_x>=row || player_next_y<0 || player_next_y>=col || grid[player_next_x][player_next_y] == '#') {
                    continue;
                }
                if(st[cur_box_pos.first][cur_box_pos.second][player_next_x][player_next_y]) continue;
                if(!can_get(cur_player_pos, cur_box_pos, {player_next_x, player_next_y})) {
                    continue;
                }
                if(box_next_x == target_pos.first && box_next_y == target_pos.second) return cur_step + 1;
                q.push({{box_next_x, box_next_y}, cur_box_pos, cur_step+1});
                st[cur_box_pos.first][cur_box_pos.second][player_next_x][player_next_y] = true;
            }
        }
        return -1;
    }
};
