from collections import deque

class Solution:
    def minPushBox(self, grid: List[List[str]]) -> int:

        R = len(grid)
        C = len(grid[0])
        maze = [['#']*(C+2)]

        for r in range(1,R+1):
            maze.append(['#'])
            str = grid[r-1]
            for c in range(1,C+1): 
                match str[c-1]:
                    case 'B':
                        Box = (r,c)
                    case 'S':
                        Source = (r,c)
                    case 'T':
                        Target = (r,c)

                if(str[c-1] == '#'):
                    maze[r].append('#')
                else:
                    maze[r].append('.')

            maze[r].append('#')

        maze.append(maze[0])

        layer = 0
        layermap = [[0]*(C+2) for _ in range(R+2)]
        ways = [[1,0], [0,1], [-1,0], [0,-1]]

        def reachable(source, target, layer):
            buffer = deque([source])

            while buffer:
                pos = buffer.popleft()

                if(pos == target):
                    return True

                next = [(pos[0]-1, pos[1]), (pos[0], pos[1]-1), (pos[0]+1, pos[1]), (pos[0], pos[1]+1)]

                for r, c in next:
                    if(maze[r][c] == '.' and layermap[r][c] != layer):
                        layermap[r][c] = layer
                        buffer.append((r,c))

            return False

        maxstep = 400
        steped = [[[0]*4 for _ in range(C+2)] for _ in range(R+2)]
        buffer = deque([(Box, Source, 0)])

        while buffer:
            box, source, step = buffer.popleft()

            if(box == Target):
                maxstep = step
                break
            if(step > maxstep):
                break

            new_boxes = [(box[0]+1, box[1]), (box[0], box[1]+1), (box[0]-1, box[1]), (box[0], box[1]-1)]
            new_sources = [(box[0]-1, box[1]), (box[0], box[1]-1), (box[0]+1, box[1]), (box[0], box[1]+1)]
            w = 0

            for push_to, push_from in zip(new_boxes, new_sources):
                if(maze[push_to[0]][push_to[1]] == '.' and maze[push_from[0]][push_from[1]] == '.' and steped[push_to[0]][push_to[1]][w] == 0):
                    maze[box[0]][box[1]] = '#'
                    layer += 1

                    if(reachable(source, push_from, layer)):
                        steped[push_to[0]][push_to[1]][w] = step+1
                        buffer.append((push_to, box, step+1))

                    maze[box[0]][box[1]] = '.'

                w += 1

        if(maxstep == 400):
            return -1

        return maxstep
