var directions = [][]int{
    []int{-1, 0}, // 0 == up
    []int{1, 0},  // 1 == down
    []int{0, -1}, // 2 == left
    []int{0, 1},  // 3 == right
}

func minPushBox(grid [][]byte) int {
    pi, pj, bi, bj, ti, tj := -1, -1, -1, -1, -1, -1

    // find start/box/target
    for i := 0; i < len(grid); i++ {
        for j := 0; j < len(grid[i]); j++ {
            if grid[i][j] == 'S' {
                pi = i
                pj = j
            } else if grid[i][j] == 'B' {
                bi = i
                bj = j
            } else if grid[i][j] == 'T' {
                ti = i
                tj = j
            }

            if pi > -1 && bi > -1 && ti > -1 {
                break
            }
        }

        if pi > -1 && bi > -1 && ti > -1 {
            break
        }
    }

    q := [][]int{[]int{pi, pj, bi, bj, 0}}
    min := -1
    visited := map[string]bool{}

    for len(q) > 0 {
        pi, pj, bi, bj, pushes := q[0][0], q[0][1], q[0][2], q[0][3], q[0][4]
        q = q[1:]

        // person can't be on wall or box
        if grid[pi][pj] == '#' || (pi == bi && pj == bj) {
            continue
        }

        // if box is at target
        if bi == ti && bj == tj {
            if min == -1 || pushes < min {
                min = pushes
            }

            continue
        }

        key := fmt.Sprintf("%d+%d+%d+%d", pi, pj, bi, bj)

        if _, ok := visited[key]; ok {
            continue
        }

        visited[key] = true

        // if person is next to box
        if di := getDirection(pi, pj, bi, bj); di > -1 {
            if isValidMove(bi, bj, di, grid) {
                q = append(q, []int{
                    pi,
                    pj,
                    bi + directions[di][0],
                    bj + directions[di][1],
                    pushes + 1,
                })
            }
        }

        for di, dir := range directions {
            if isValidMove(pi, pj, di, grid) {
                q = append(q, []int{pi + dir[0], pj + dir[1], bi, bj, pushes})
            }
        }
    }

    return min
}

func isValidMove(i int, j int, di int, grid [][]byte) bool {
    x := i + directions[di][0]
    y := j + directions[di][1]

    return x > -1 && x < len(grid) && y > -1 && y < len(grid[x]) && grid[x][y] != '#'
}

func getDirection(ai int, aj int, bi int, bj int) int {
    for di, dir := range directions {
        if ai + dir[0] == bi && aj + dir[1] == bj {
            return di
        }
    }

    return -1
}
