# class Solution {
    public int minPushBox(char[][] grid) {
        /*
        The problem asks for minimum moves - we have to BFS
        valid moves - 
                     - space for man to stand - this should be in adjascent cell to box
                     - space in the opposite direction for the box to move.
                    - check if the person can be moved from his previous position to current location from where he is supposed to push the box - this can be checked using DFS(canManReach() in below code).
                    - if move is possible, move the box to adjascent cell, place the person in the earlier position of the box.
        */

        int[] box = null;
        int[] player = null;
        int[] target = null;
        for(int i=0; i<grid.length; i++){
            for(int j=0; j<grid[0].length; j++){
                if(grid[i][j] == 'B'){
                    box = new int[]{i, j};
                    grid[i][j] = '.';
                }
                if(grid[i][j] == 'S'){
                    player = new int[]{i, j};
                     grid[i][j] = '.';
                }
                if(grid[i][j] == 'T'){
                    target = new int[]{i, j};
                     grid[i][j] = '.';
                }
            }
        }

        LinkedList<int[]> queue = new LinkedList<>();
        queue.addFirst(new int[]{box[0], box[1], player[0], player[1], 0});
        HashSet<String> set = new HashSet<>();

        //BFS
        while(queue.isEmpty() == false){
            int[] curr = queue.removeLast();
            //System.out.println(Arrays.toString(curr));

            if(curr[0] == target[0] && curr[1] == target[1]){
                return curr[4];
            }
            if(set.contains(curr[0]+"/"+curr[1]+"/"+curr[2]+"/"+curr[3])){
                continue;
            }
            set.add(curr[0]+"/"+curr[1]+"/"+curr[2]+"/"+curr[3]);
            //check if up movement is possible
            if(curr[0] < grid.length-1 && curr[0] > 0){
                if(grid[curr[0]+1][curr[1]] == '.' && canManReach(curr[2], curr[3], curr[0]+1, curr[1], grid, new boolean[grid.length][grid[0].length], curr[0], curr[1]) && grid[curr[0]-1][curr[1]] == '.'){
                    queue.addFirst(new int[]{curr[0]-1, curr[1], curr[0], curr[1], curr[4]+1});
                }
            }

            //check if down movement is possible
            if(curr[0] < grid.length-1 && curr[0] > 0){
                if(grid[curr[0]+1][curr[1]] == '.' && canManReach(curr[2], curr[3], curr[0]-1, curr[1], grid, new boolean[grid.length][grid[0].length], curr[0], curr[1]) && grid[curr[0]-1][curr[1]] == '.'){
                    queue.addFirst(new int[]{curr[0]+1, curr[1], curr[0], curr[1], curr[4]+1});
                }
            }

            //check if right movement is possible
            if(curr[1] < grid[0].length-1 && curr[1] > 0){
                if(grid[curr[0]][curr[1]+1] == '.' && canManReach(curr[2], curr[3], curr[0], curr[1]-1, grid, new boolean[grid.length][grid[0].length], curr[0], curr[1]) && grid[curr[0]][curr[1]-1] == '.'){
                    queue.addFirst(new int[]{curr[0], curr[1]+1, curr[0], curr[1], curr[4]+1});
                }
            }

            //check if left movement is possible
            if(curr[1] < grid[0].length-1 && curr[1] > 0){

                if(grid[curr[0]][curr[1]+1] == '.' && canManReach(curr[2], curr[3], curr[0], curr[1]+1, grid, new boolean[grid.length][grid[0].length], curr[0], curr[1]) && grid[curr[0]][curr[1]-1] == '.'){
                    //System.out.println("AAAAAAA");
                    queue.addFirst(new int[]{curr[0], curr[1]-1, curr[0], curr[1], curr[4]+1});
                }
            }

        }

        return -1;
    }

    boolean canManReach(int x, int y, int newX, int newY, char[][] grid, boolean[][] visited, int bx, int by){
        if(x<0 || x>grid.length-1 || y<0 || y>grid[0].length-1){
            return false;
        }
        if(grid[x][y] == '#'){
            return false;
        }
        if(x == bx && y == by){
            return false;
        }
        if(x == newX && y == newY){
            return true;
        }
        if(visited[x][y] == true){
            return false;
        }

        visited[x][y] = true;

        boolean left = canManReach(x, y-1, newX, newY, grid, visited, bx, by);
