class Solution:
	def minPushBox(self, grid: List[List[str]]) -> int:
		n,m=len(grid),len(grid[0])
		for i in range(n):
			for j in range(m):
				if grid[i][j]=='S':
					player=[i,j]
				elif grid[i][j]=='T':
					target=[i,j]
				elif grid[i][j]=='B':
					box=[i,j]
		q=deque([[player[0],player[1],box[0],box[1],0]])
		vis=set([(player[0],player[1],box[0],box[1])])
		while q:
			i,j,bi,bj,steps=q.pop()
			if [bi,bj]==target:
				return steps
			for dx,dy in [(0,1),(1,0),(-1,0),(0,-1)]:
