/**
 * Time Complexity: O(rows * cols)
 * Space Complexity: O(rows * cols)
 * where `rows` is the number of the rows of the grid `grid`
 *       `cols` is the number of the columns of the grid `grid`
 */
class Solution {
 private:
  static constexpr int directions[] = {0, 1, 0, -1, 0};
  static constexpr int n_directions = 4;
  static constexpr char player = 'S';
  static constexpr char wall = '#';
  static constexpr char target = 'T';
  static constexpr char box = 'B';
  static constexpr int n_positions = 3;   // initial player's position, initial box's position, target position
  using position_t = pair<int, int>;
  using positions_t = array<position_t, n_positions>;
  using q_node_t = tuple<int, int, int>;   // {the row, the col, the direction}

 public:
  int minPushBox(const vector<vector<char>> &grid) {
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    const auto [initial_player_position, initial_box_position, target_position] = get_positions(grid);
    queue<q_node_t> q;
    bool visited[rows][cols][n_directions];
    memset(visited, 0, sizeof(visited));
    for (int d = 0; d < n_directions; ++d) {
      const position_t player_destination = get_adjacent_position(initial_box_position, d, true);
      if (can_visit(grid, initial_box_position, player_destination) &&
          reachable(grid, initial_box_position, initial_player_position, player_destination)) {
        q.emplace(initial_box_position.first, initial_box_position.second, d);
        visited[initial_box_position.first][initial_box_position.second][d] = true;
        break;
      }
    }

    int step = 0;
    while (!q.empty()) {
      ++step;
      for (size_t qs = q.size(); qs > 0; --qs) {
        const auto [current_box_row, current_box_col, current_box_direction] = q.front();
        q.pop();
        const position_t current_box_position{current_box_row, current_box_col};
        const position_t current_player_position = get_adjacent_position(current_box_position,
                                                                         current_box_direction,
                                                                         true);
        for (int d = 0; d < n_directions; ++d) {
          const position_t next_box_position = get_adjacent_position(current_box_position, d, false);
          const position_t next_player_position = get_adjacent_position(current_box_position, d, true);
          if (!can_visit(grid, current_box_position, next_box_position) ||
              !can_visit(grid, current_box_position, next_player_position) ||
              visited[next_box_position.first][next_box_position.second][d] ||
              !reachable(grid, current_box_position, current_player_position, next_player_position)) {
            continue;
          }
          if (target_position == next_box_position) {
            return step;
          }
          q.emplace(next_box_position.first, next_box_position.second, d);
          visited[next_box_position.first][next_box_position.second][d] = true;
        }
      }
    }
    return -1;
  }

 private:
  positions_t get_positions(const vector<vector<char>> &grid) {
    constexpr char findings[] = {player, box, target};
    constexpr int n_findings = sizeof(findings) / sizeof(char);
    constexpr unsigned short full_mask = (1 << n_findings) - 1;
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    unsigned short mask = 0U;
    positions_t ret;
    for (int r = 0; r < rows && mask != full_mask; ++r) {
      for (int c = 0; c < cols && mask != full_mask; ++c) {
        const char cell = grid[r][c];
        int index = static_cast<int>(find(findings, findings + n_findings, cell) - findings);
        if (index < n_findings) {
          ret[index] = make_pair(r, c);
          mask |= (1 << index);
        }
      }
    }
    return ret;
  }

  position_t get_adjacent_position(const position_t &position,
                                   const int d,
                                   const bool reversed) {
    const int coefficient = reversed ? -1 : 1;
    return make_pair(position.first + coefficient * directions[d],
                     position.second + coefficient * directions[d + 1]);
  }

  bool reachable(const vector<vector<char>> &grid,
                 const position_t &box_position,
                 const position_t &source,
                 const position_t &target) {
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    bool visited[rows * cols];
    memset(visited, 0, sizeof(visited));
    return dfs(grid, box_position, source, target, visited);
  }

  bool dfs(const vector<vector<char>> &grid,
           const position_t &box_position,
           const position_t &current,
           const position_t &target,
           bool *visited) {
    if (current == target) {
      return true;
    }

    const int cols = static_cast<int>(grid.front().size());
    visited[current.first * cols + current.second] = true;
    for (int d = 0; d < n_directions; ++d) {
      const position_t next = get_adjacent_position(current, d, false);
      if (can_visit(grid, box_position, next) &&
          !visited[next.first * cols + next.second] &&
          dfs(grid, box_position, next, target, visited)) {
        return true;
      }
    }
    return false;
  }

  bool can_visit(const vector<vector<char>> &grid,
                 const position_t &box_position,
                 const position_t &current) {
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    return current.first >= 0 &&
      current.first < rows &&
      current.second >= 0 &&
      current.second < cols &&
      current != box_position &&
      grid[current.first][current.second] != wall;
  }
};
