class Solution {
public:
int dx[4]={0,0,1,-1};
int dy[4]={1,-1,0,0};
int m,n;

bool check(int x,int y,vector<vector<char>>& grid ){
return (x>=0 && x<m ) && (y>=0 && y<n) && (grid[x][y]!='#');
}


bool possible(int px,int py,int npx,int npy,int bx,int by,vector<vector<char>>& grid){
    queue<vector<int>>q;
     vector<vector<bool>>vis(m,vector<bool>(n,false));
     vis[px][py]=true;
    q.push({px,py});
    while(!q.empty()){
        auto it=q.front();
        q.pop();
        if(it[0]==npx && it[1]==npy){
            return true;
        }
        for(int i=0;i<4;i++){
            int x=it[0]+dx[i],y=it[1]+dy[i];
            if(check(x,y,grid) && !(x==bx && y==by) && vis[x][y]==false){
                vis[x][y]=true;
                q.push({x,y});
            }
        }

    }
    return false;

}
